function setSnapClientMuted(n,t,i){$(n).toggleClass("spinner");$.ajax({type:"POST",url:"api/snap/snapclientmute",contentType:"application/json; charset=utf-8",dataType:"json",data:JSON.stringify({clientMac:t,muted:i})})}function setSnapClientGlobalVolume(n,t){$(".current-volume").addClass("spinner");$.ajax({type:"POST",url:"api/snap/snapclientglobalvolume",contentType:"application/json; charset=utf-8","async":!0,dataType:"json",data:JSON.stringify({PercentagePointChange:t})})}function mopidyTogglePlay(n){$.ajax({type:"POST",url:"api/mopidy/togglepause",contentType:"application/json; charset=utf-8",dataType:"json",success:t=>{n(t.state)}})}function mopidyPlayUri(n,t){$.ajax({type:"POST",url:"api/mopidy/playepisode",data:JSON.stringify({uri:n}),contentType:"application/json; charset=utf-8",dataType:"json",success:n=>{t&&t(n.state)}})}function reloadMe(n,t){setTimeout(function(){$(n).parent().load(t)},500)}function lightingColour(){$.ajax({type:"POST",url:"api/lighting/colour",data:JSON.stringify({red:10,green:1,blue:0,white:0}),contentType:"application/json; charset=utf-8",dataType:"json"})}function lightingMode(){$.ajax({type:"POST",url:"api/lighting/mode",data:JSON.stringify({mode:"off"}),contentType:"application/json; charset=utf-8",dataType:"json"})}