function setSnapClientMuted(n,t,i){$(n).toggleClass("spinner");$.ajax({type:"POST",url:"api/snap/snapclientmute",contentType:"application/json; charset=utf-8",dataType:"json",data:JSON.stringify({clientMac:t,muted:i})})}function setSnapClientGlobalVolume(n,t){$(".current-volume").addClass("spinner");$.ajax({type:"POST",url:"api/snap/snapclientglobalvolume",contentType:"application/json; charset=utf-8","async":!0,dataType:"json",data:JSON.stringify({PercentagePointChange:t}),success:()=>{document.getElementById("linkid-refreshsnapclients").click()},error:()=>{document.getElementById("linkid-refreshsnapclients").click()}})}function mopidyTogglePlay(n){$.ajax({type:"POST",url:"api/mopidy/togglepause",contentType:"application/json; charset=utf-8",dataType:"json",success:t=>{n(t.state)}})}function mopidyPlayUri(n,t){$.ajax({type:"POST",url:"api/mopidy/playepisode",data:JSON.stringify({uri:n}),contentType:"application/json; charset=utf-8",dataType:"json",success:n=>{t&&t(n.state)}})}function mopidyPlayRandomEpisode(n,t){$.ajax({type:"POST",url:"api/mopidy/playrandomepisode",contentType:"application/json; charset=utf-8",dataType:"json",success:n=>{t&&t(n.state)}})}function reloadMe(n,t){setTimeout(function(){$(n).parent().load(t)},500)}